//Dependencias
import React, { Component } from 'react';
import L from 'leaflet';
import 'leaflet-routing-machine';
import Popup from 'reactjs-popup';


//Componentes
import '../styles.css';
import Ruta from '../images/placeholder.png'
import Cancel from '../images/cancel-button.png'

//Variables globales
var myIcon = L.icon({
  iconUrl: 'data:image/png;base64,',
  iconSize: [25,41]
});

var myFavorites = L.icon({
  iconUrl: 'data:image/svg+xml;utf8;base64,',
  iconSize: [60,30]
});

var map;
var routingline;
var markerorigen;
var markerdestino;
var coordstemp;
var markertemp;

class Maps extends Component {

    constructor(props){
        super(props);

        //Metodos
        this.initMap = this.initMap.bind(this);
        this.handleRightClick = this.handleRightClick.bind(this);
        this.addFavorites = this.addFavorites.bind(this);
        this.openModal = this.openModal.bind(this);
        this.closeModal = this.closeModal.bind(this);
        this.updateInput = this.updateInput.bind(this);

        this.state = {
            modal: {
                nombrefavorite: '',
                open: false,
                saveFavorite: 0
            },
            location: {
                lat: 0,
                lng: 0
            },
            favorites: [],
            zoom: 2,
            waypoints: {
                activo: 0,
                origen: {},
                destino: {}
              }
        }

    }


    //Metodo que se ejecuta despues de que todo este en el DOM
    componentDidMount () {       

        if (navigator.geolocation){
        navigator.geolocation.getCurrentPosition((Position) => {

            this.setState({
            location: {
                lat: Position.coords.latitude,
                lng: Position.coords.longitude
            },
            zoom: 16
            })
            this.initMap()
        });
        }

    }


    //Crea el mapa
    initMap() {

        map = L.map('map', {
        center: [this.state.location.lat, this.state.location.lng],
        zoom: this.state.zoom
        });

        L.tileLayer('http://{s}.tile.osm.org/{z}/{x}/{y}{r}.png', {
        attribution: 'Â© OpenStreetMap contributors'
        }).addTo(map);

        //Posicion actual
        L.marker([this.state.location.lat, this.state.location.lng], {icon: myIcon}).addTo(map)
            .on("click", (e) => {
                console.log(e.latlng.lat, e.latlng.lng);
                console.log("Me oprimiste");
            })
            .on("contextmenu", (e) => {
            })
            .bindPopup("Tu posicion actual").openPopup();

        this.initFavorite();        

        //Agregando marcadores o indicando la ruta
        map.on('contextmenu', (e) => {

            this.handleRightClick(e);


        });

    }


    //Manejador de evento del click izquierdo
    handleRightClick(e) {

        coordstemp = e;
        this.openModal()
    }

    createRoute(origen, destino) {
        var router = new L.Routing.OSRMv1({});

        var routewaypoints = [
        L.Routing.waypoint(L.latLng(origen.lat, origen.lng)),
        L.Routing.waypoint(L.latLng(destino.lat, destino.lng))]

        router.route(routewaypoints, function(error, routes) {
        routingline = L.Routing.line(routes[0]).addTo(map);
        console.log('Distance: ' + (routes[0].summary.totalDistance / 1000) + ' Km');
        console.log('Time: ' + (routes[0].summary.totalTime / 60) + ' Min');}, 
        null, {});
    }


    addFavorites(nombre) {

        var favorites = this.state.favorites;
        var temporal = [coordstemp.latlng.lat, coordstemp.latlng.lng];
          favorites.push({
            nombre: this.state.modal.nombrefavorite,
            lat: temporal[0],
            lng: temporal[1]
        });
        this.setState({favorites});
        L.marker([coordstemp.latlng.lat, coordstemp.latlng.lng],{icon: myFavorites}).addTo(map)
          .on("click", (e) => {

        })
        .on("contextmenu", (e) => {
        })
        .bindPopup(nombre)
        .openPopup();
    }


    //Metodo que me genera los favoritos guardados
    initFavorite() {

        //Favoritos
        if(this.state.favorites.length > 0)
        {
          for(let element of this.state.favorites){
            L.marker([element.lat, element.lng], {icon: myFavorites}).addTo(map)
              .on("click", (e) => {

            })
            .on("contextmenu", (e) => {
            })
            .bindPopup(element.nombre)
            .openPopup();
          }
        }
    }


    //Metodos que se encargan de abrir y cerrar el popup
    openModal (){
        this.setState({ modal: {
            nombrefavorite: '',
            open: true,
            saveFavorite: 0
        }})
    }

    closeModal () {

        this.setState({modal: {
            nombrefavorite: '',
            open: false,
            saveFavorite: 0
        }})
    }


    //Metodo que me actualiza el nombre del lugar
    updateInput(event) {
        console.log(event)
        this.setState({modal: {
            nombrefavorite: event.target.value,
            open: true,
            saveFavorite: 1}})    
    }


    render() {

        var componentePop;

        if(this.state.waypoints.activo === 0){
            if(this.state.modal.saveFavorite === 0){

                componentePop =
                        <div>
                            <h3 className="modal-text">Â¿QuÃ© deseas hacer?</h3>
                            <button className="button-modal"
                                onClick={ () => {

                                    this.setState({modal: {
                                        nombrefavorite: '',
                                        open: true,
                                        saveFavorite: 1
                                    }})
                            }}>Guardar favorito</button>
                            <button className="button-modal"
                                onClick={ () => {

                                    var temporal = [coordstemp.latlng.lat, coordstemp.latlng.lng];
                                    this.setState({
                                        waypoints: {
                                            activo: 1,
                                            origen: {},
                                            destino: {
                                                lat: temporal[0],
                                                lng: temporal[1]
                                            }
                                        }
                                    })
                                    this.closeModal();
                            }}>Ir hacia aqui</button>
                            <button className="button-modal"
                                onClick={ () => {

                                    var temporal = [coordstemp.latlng.lat, coordstemp.latlng.lng];
                                    this.setState({
                                        waypoints: {
                                            activo: 1,
                                            origen: {
                                                lat: temporal[0],
                                                lng: temporal[1]},
                                            destino: {}
                                        }
                                    })
                                    this.closeModal();
                            }}>Ir desde aqui</button>
                            <button className="button-modal"
                                onClick={ () => {

                                    this.closeModal();
                            }}>Cancelar</button>
                        </div>;
            }                 

            if(this.state.modal.saveFavorite === 1){

                componentePop = 
                        <div>
                            <h3 className="modal-text">Ingresa el nombre del lugar:</h3>
                            <input className="input-favorite" onChange={this.updateInput} id="nameFavorite" type="text" placeholder="Nombre del lugar"/>
                            <button className="button-modal"
                                onClick={ () => {
                                    this.addFavorites(this.state.modal.nombrefavorite);
                                    this.closeModal();
                            }}>Guardar favorito</button>
                            <button className="button-modal"
                                onClick={ () => {
                                    this.closeModal();
                            }}>Cancelar</button>
                        </div>

            }
        }


        return(
            <div>
                <div id="map" className="map"></div>
                <Popup
                    className="container-modal"
                    open={this.state.modal.open}
                    closeOnDocumentClick={false}
                    onClose={this.closeModal}
                    >
                    {componentePop}
                </Popup>
            </div>

        );
    }

}

export default Maps;
