//Dependencias
import React, { Component } from 'react';
import { Link } from 'react-router-dom';


//Componentes
import Taxi from '../images/Taxi.svg';
import '../styles.css';


class Main extends Component{

    state = {
        ventana: 0
    }


    render(){

        var windowCurrent;

        if(this.state.ventana == 0){

            windowCurrent = 
            <div className="main">
                <div className="container">
                    <img className="image-taxi" src={Taxi} alt="Taxi" width="300"/>
                </div>
                <div className="container box-login">
                    <h1 className="initial-text">
                        Registrate para tener a la mano la mejor opción de transporte.<br/><br/>
                            ¡Pidelo desde cualquier lugar!
                    </h1>
                    <div className="container-buttons">
                        <button onClick={
                            () => {
                                this.setState({ventana: 2})
                            }
                        } className="login-text button-a">
                                Registrarse como socio
                        </button>
                        <button onClick={
                            () => {
                                this.setState({ventana: 2})
                            }
                        } className="login-text button-a">
                                Registrarse como usuario
                        </button>
                        <button onClick={
                            () => {
                                this.setState({ventana:1})
                            }
                        } className="login-text button-b" >
                                ¿Tienes una cuenta? Inicia sesión
                        </button>
                    </div>                                     
                </div>
            </div>
        }
        else if (this.state.ventana == 1){

            windowCurrent = 
            <div className="main">
                <div className="container">
                    <img className="image-taxi" src={Taxi} alt="Taxi" width="300"/>
                </div>
                <div className="container box-login">
                        <h1 className="login-title">Iniciar Sesión</h1>
                        <form className="form-login">
                            <input className="field-login text-field" id="celular" type="text" placeholder="Celular"/> 
                            <input className="field-login text-field" id="password" type="password" placeholder="Contraseña"/>  
                            <input className="login-button text-field" type="submit" value="Iniciar Sesión"/>
                        </form>                                                                
                </div>

            </div>
        }
        else if (this.state.ventana == 2){

            windowCurrent = 
            <div className="container-forms"> 
                <h1 className="text-forms">Registrate para continuar</h1> 
                <form className="form-users">                                       
                    <input className="field name text-field" id="name" type="text" placeholder="Nombre"/>
                    <input className="field name text-field" id="last-name" type="text" placeholder="Apellido"/>
                    <input className="field other text-field" id="cellphone" type="text" placeholder="Celular"/>
                    <input className="field other text-field" id="address" type="text" placeholder="Dirección"/>
                    <input className="field other text-field" id="nrocard" type="text" placeholder="Número de tarjeta"/>

                    <input className="button-forms" type="submit" value="Seguir"/>                    
                </form>                    
            </div> 
        }
        else if (this.state.ventana == 3){

            windowCurrent = 
            <div className="container-forms"> 
                <h1 className="text-forms">Registrate para continuar</h1> 
                <form className="form-users">                                       
                    <input className="field name text-field" id="name" type="text" placeholder="Nombre"/>
                    <input className="field name text-field" id="last-name" type="text" placeholder="Apellido"/>
                    <input className="field other text-field" id="cellphone" type="text" placeholder="Celular"/>
                    <input className="field other text-field" id="address" type="text" placeholder="Dirección"/>
                    <input className="field other text-field" id="typeId" type="text" placeholder="Tipo de identificación"/>
                    <input className="field other text-field" id="NroIdentificacion" type="text" placeholder="Número de identificación"/>
                    <div>
                        <input className="field date text-field" id="day" type="text" placeholder="Día"/>
                        <input className="field date text-field" id="month" type="text" placeholder="Mes"/>
                        <input className="field date text-field" id="year" type="text" placeholder="Año"/>
                    </div>
                    <input className="button-forms" type="submit" value="Seguir"/>
                </form>                    
            </div>  
        }

        return(
            <div>
                {windowCurrent}
            </div>);
    }
}

export default Main;
