//Dependencias
import React, { Component } from 'react';
import { BrowserRouter as Router} from 'react-router-dom';
import { Route, Redirect } from 'react-router-dom';

//Componentes
import Main from './components/main/main';
import Login from './components/main/login';
import Navigation from './components/navigation';
import Footer from './components/footer';
import FormClient from './components/forms/client';
import FormDriver from './components/forms/driver';
import Maps from './components/map/maps';
import WindowDriver from './components/window/windowDriver';
import WindowUser from './components/window/windowUser';
import ErrorPage from './components/errorpage';


var isAuthenticated = false;


class App extends Component{  

  constructor(props) {
    super(props);

    this.state = {
      message: ''
    }


  }


  componentDidMount() {
    fetch('/main')
    .then(res => res.json())
    .then(res => this.setState({message: res.status}))
    .catch(err => console.log(err));



  }


  render() {

    return(
      <div>
        <Navigation />
        <Router>
          <div>
                <Route exact path="/" render={() => {
                  if(isAuthenticated == false){
                    return <Main />
                  }
                  else {
                    return<Redirect to='/MainUser' />
                  }

                }}/>
                <Route exact path="/Login" render={() => {
                  if(isAuthenticated == false){
                    return <Main />
                  }
                  else {
                    return<Redirect to='/MainUser' />
                  }

                }}/>
                <Route exact path="/FormUser" render={() => {
                  if(isAuthenticated == false){
                    return <Main />
                  }
                  else {
                    return<Redirect to='/MainUser' />
                  }

                }}/>
                <Route exact path="/FormDriver" render={() => {
                  if(isAuthenticated == false){
                    return <Main />
                  }
                  else {
                    return<Redirect to='/MainUser' />
                  }

                }}/>

                <Route exact path="/Maps" render={() => {
                  if(isAuthenticated == true){
                    return <Maps />
                  }
                  else {
                    return<Redirect to='/' />
                  }

                }}/>
                <Route exact path="/MainDriver" render={() => {
                  if(isAuthenticated == true){
                    return <WindowDriver />
                  }
                  else {
                    return<Redirect to='/' />
                  }

                }}/>
                <Route exact path="/MainUser" render={() => {
                  if(isAuthenticated == true){
                    return <WindowUser />
                  }
                  else {
                    return<Redirect to='/' />
                  }

                }}/>
          </div>
        </Router>        
        <Footer />
      </div>

    );
  }
}

export default App;
