const initialState = {
    User: {
        Id: 0,
        Logged: false,
    }
}

//Reducer
const reducer = (state = initialState, action) => {
    if(action.type == "LOGGED"){

        return [{
            User: {
                Id: 1,
                Logged: true
            }
        }]
    }
    else if (action.type == "SIGN_OUT"){

        return [{
            User: {
                Id: 0,
                Logged: false
            }
        }]
    }
    else{

        return state;
    }

}

export default reducer;
