//Dependencias
import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Redirect} from 'react-router-dom';


//Componentes
import Navigation from './components/navigation';
import Footer from './components/footer';
import Main from './components/main/main';
import Login from './components/main/login';
import FormClient from './components/forms/client';
import FormDriver from './components/forms/driver';
import Maps from './components/map/maps';
import WindowDriver from './components/window/windowDriver';
import WindowUser from './components/window/windowUser';



const PrivateRoute = ({component : Component, isLogged, ...rest }) => {

  return (
    <Route {...rest}
    render={() => {
      if(isLogged === "User is logged"){
        return (<Component />);
      }else {
        return (< Redirect to="/Login"/>);
      }
    }}

    />
  );
}


export default class App extends Component {

  constructor() {
    super()

    this.state = {
      security: {
        isLogged: "User is not logged"
      }
    }

    this.componentWillMount = this.componentWillMount.bind(this);
    this.render = this.render.bind(this);
  }

  componentWillMount() {

    fetch('/main')
    .then(res => res.json())
    .then(res => this.setState({security: {isLogged: res.status}}))
    .catch(err => console.log(err));

    this.createApp();
  }


    createApp() {
      return(
        <div>
          <Navigation/> 
            <Router>
              <div>
                <Route exact path="/" component={Main}/>
                <Route exact path="/Login" component={Login}/>
                <Route exact path="/FormUser" component={FormClient}/>
                <Route exact path="/FormDriver" component={FormDriver}/>
                <PrivateRoute exact path="/Maps" isLogged={this.state.security.isLogged} component={Maps}/>
                <Route exact path="/MainDriver" component={WindowDriver}/>
                <Route exact path="/MainUser" component={WindowUser}/>    
              </div>
            </Router>
          <Footer />
        </div>      
      );
    }

};
