//Dependencias
import React, { Component } from 'react';
import { BrowserRouter as Router} from 'react-router-dom';
import { Route } from 'react-router-dom';

//Componentes
import Main from './components/main/main';
import Login from './components/main/login';
import Navigation from './components/navigation';
import Footer from './components/footer';
import FormClient from './components/forms/client';
import FormDriver from './components/forms/driver';
import Maps from './components/map/maps';
import WindowDriver from './components/window/windowDriver';
import WindowUser from './components/window/windowUser';



class App extends Component{  

/*

  componentDidMount() {
    fetch('/backend')
    .then(res => res.json())
    .then(status => this.setState({status}, console.log(status)));

  }
*/

  render() {

    return(
      <div>
        <Navigation />
        <Router>
          <div>
          <Route exact path="/" component={Main}/>
          <Route exact path="/Login" component={Login}/>
          <Route exact path="/FormClient" component={FormClient}/>
          <Route exact path="/Login" component={FormDriver}/>
          <Route exact path="/" component={Maps}/>
          <Route exact path="/Login" component={Login}/>
          </div>
        </Router>        
        <Footer />
      </div>

    );
  }
}

export default App;

