  }
*/


  render() {
    return(
      <div>
        <Navigation />
        <Router>
          <div>
          <Route path="/" exact render={
              () => {

                if(this.state.ventana == "Main"){

                  return (<Main />);
                }
                else if (this.state.ventana == "Login"){

                  return(<Login />);
                }
                else if (this.state.ventana == "Form-Client"){

                  return(<Login />);
                }
                else if (this.state.ventana == "Form-Driver"){

                  return(<Login />);
                }
                else if (this.state.ventana == "Maps"){

                  return(<Login />);
                }

              }
          }/>
          </div>
        </Router>        
        <Footer />
      </div>

    );
  }
}

export default App;

