//Dependencias
import React, { Component } from 'react';
import L from 'leaflet';
import 'leaflet-routing-machine';
import Popup from 'reactjs-popup';


//Componentes
import '../styles.css';
import Ruta from '../images/placeholder.png'
import Cancel from '../images/cancel-button.png'

//Variables globales
var myIcon = L.icon({
  iconUrl: 'data:image/png;base64,',
  iconSize: [25,41]
});

var myFavorites = L.icon({
  iconUrl: 'data:image/svg+xml;utf8;base64,',
  iconSize: [60,30]
});

var map;
var routingline;
var markerorigen;
var markerdestino;
var coordstemp;


class Maps extends Component {

  //Constructor
  constructor(props) {
    super(props);

    this.createRouting = this.createRouting.bind(this);
    this.deleteRouting = this.deleteRouting.bind(this);
    this.initMap = this.initMap.bind(this);
    this.openFavorite = this.openFavorite.bind(this);
    this.closeFavorite = this.closeFavorite.bind(this);
    this.openRoute = this.openRoute.bind(this);
    this.closeRoute = this.closeRoute.bind(this);
    this.addFavorites = this.addFavorites.bind(this);
    this.changeStateFavorites = this.changeStateFavorites.bind(this);
    this.updateInput = this.updateInput.bind(this);
    this.initFavorite =  this.initFavorite.bind(this);  
    this.handleRightClick = this.handleRightClick.bind(this);

    this.state = {
      openF: false,
      openR: false,
      openRF: false,
      location: {
        lat: 0,
        lng: 0
      },
      saveFavorites : 0,
      nombrefavorite: '',
      favorites: [],
      zoom: 2,
      waypoints: {
        activo: 0,
        origen: {},
        destino: {}
      }
    }

  } 

  //Metodo que se ejecuta despues de que todo este en el DOM
  componentDidMount () {       

    if (navigator.geolocation){
      navigator.geolocation.getCurrentPosition((Position) => {

        this.setState({
          location: {
            lat: Position.coords.latitude,
            lng: Position.coords.longitude
          },
          zoom: 16
        })
        this.initMap()
      });
    }

  }


  //Crea el mapa
  initMap() {

    map = L.map('map', {
      center: [this.state.location.lat, this.state.location.lng],
      zoom: this.state.zoom
    });

    L.tileLayer('http://{s}.tile.osm.org/{z}/{x}/{y}{r}.png', {
      attribution: 'Â© OpenStreetMap contributors'
    }).addTo(map);

    //Posicion actual
    L.marker([this.state.location.lat, this.state.location.lng], {icon: myIcon}).addTo(map)
      .on("click", (e) => {
        console.log(e.latlng.lat, e.latlng.lng);
        console.log("Me oprimiste");
    })
      .bindPopup("Tu posicion actual").openPopup();

    this.initFavorite();        

    //Agregando marcadores o indicando la ruta
    map.on('contextmenu', (e) => {

      this.handleRightClick(e);

    });

  }

  handleRightClick(e) {

    if(this.state.waypoints.activo){

      if (this.state.waypoints.origen == undefined)
      {
        this.setState({
          waypoints: {
            activo: 1,
            origen: {
              lat: e.latlng.lat, 
              lng: e.latlng.lng}
        }});
        markerorigen = L.marker([e.latlng.lat, e.latlng.lng],{icon: myIcon}).addTo(map)
      }
      else if (this.state.waypoints.destino == undefined){
        this.setState({
          waypoints: {
            activo: 1,
            origen: {
              lat: this.state.waypoints.origen.lat,
              lng: this.state.waypoints.origen.lng,

            },
            destino: {
              lat: e.latlng.lat, 
              lng: e.latlng.lng}
        }});

        markerdestino = L.marker([e.latlng.lat, e.latlng.lng],{icon: myIcon}).addTo(map)

        this.openRoute();

        var router = new L.Routing.OSRMv1({});

        var routewaypoints = [
          L.Routing.waypoint(L.latLng(this.state.waypoints.origen.lat,this.state.waypoints.origen.lng)),
          L.Routing.waypoint(L.latLng(this.state.waypoints.destino.lat,this.state.waypoints.destino.lng))]

        router.route(routewaypoints, function(error, routes) {
          routingline = L.Routing.line(routes[0]).addTo(map);
          console.log('Distance: ' + (routes[0].summary.totalDistance / 1000) + ' Km');
          console.log('Time: ' + (routes[0].summary.totalTime / 60) + ' Min');}, 
        null, {});

      }
    }
    else {

      coordstemp = e;
      this.openFavorite();
    }
  }

  initFavorite() {

    //Favoritos
    if(this.state.favorites.length > 0)
    {
      for(let element of this.state.favorites){
        L.marker([element.lat, element.lng], {icon: myFavorites}).addTo(map)
          .on("click", (e) => {
            console.log(e.latlng.lat, e.latlng.lng);
            console.log("Me oprimiste");
        })
          .bindPopup(element.nombre).openPopup();
      }
    }
  }

  //Metodo que activa el modo ruta
  createRouting() {

    if (this.state.waypoints.activo == false){
      this.setState({
      waypoints: {
        activo: 1
      }
    });
    }


  }

  //Metodo que elimina la ruta actual
  deleteRouting() {

    if(this.state.waypoints.activo){
      map.removeLayer(routingline);
      map.removeLayer(markerorigen);
      map.removeLayer(markerdestino);

      this.setState({
        waypoints: {
          activo: 0,
          origen: {},
          destino: {}
        }
      });
    }

  }

  changeStateFavorites() {
    if (this.state.saveFavorites === 0){
      this.setState({saveFavorites: 1});
    }
    else {
      this.setState({saveFavorites: 0});
    }

  }

  addFavorites(nombre) {

    var favorites = this.state.favorites;
    var temporal = [coordstemp.latlng.lat, coordstemp.latlng.lng];
      favorites.push({
        nombre: nombre,
        lat: temporal[0],
        lng: temporal[1]
    });
    this.setState({favorites});
    L.marker([coordstemp.latlng.lat, coordstemp.latlng.lng],{icon: myFavorites}).addTo(map)
      .on("click", (e) => {
      console.log(e.latlng.lat, e.latlng.lng);
      console.log("Me oprimiste");
    })
      .bindPopup(nombre).openPopup();
  }

  openFavorite (){
    this.setState({ openF: true })
  }

  closeFavorite () {
    this.setState({ openF: false })
  } 

  openRoute (){
    this.setState({ openR: true })
  }

  closeRoute () {
    this.setState({ openR: false })
  } 

  updateInput(event) {
    this.setState({nombrefavorite : event.target.value})

  }

  render() {


    var componeFavorite;
    var componeRuta;

    if(this.state.saveFavorites === 0){
      componeFavorite = 
        <div>
          <h3 className="favorite-text">Â¿Deseas guardar este destino en tus favoritos?</h3>
          <button onClick={() => {
            this.changeStateFavorites();
          }} className="button-favorite">Guardar favorito</button>
          <button onClick={this.closeFavorite} className="button-favorite">No guardar</button>
      </div>
    }
    else if (this.state.saveFavorites === 1){
      componeFavorite = 
        <div>
            <h3 className="favorite-text">Ingrese el nombre del lugar</h3>
            <input className="input-favorite" onChange={this.updateInput} id="nameFavorite" type="text" placeholder="Nombre del lugar"/>
            <button onClick={() => {
              console.log(this.state.nombrefavorite);
              this.addFavorites(this.state.nombrefavorite);
              this.changeStateFavorites();
              this.closeFavorite();
            }} className="button-favorite">Guardar favorito</button>
        </div>
    }

    return (
        <div> 
          <img onClick={this.createRouting} className="image-ruta" src={Ruta} alt="Ruta" width="40"/>
          <img onClick={this.deleteRouting} className="image-cancel" src={Cancel} alt="Cancel" width="40"/>
          <div id="map" className="map"></div>
          <Popup
            className="container-favorite"
            open={this.state.openF}
            closeOnDocumentClick={false}
            >
            {componeFavorite}
          </Popup>
          <Popup
            className="container-favorite"
            open={this.state.openRF}
            closeOnDocumentClick={false}
            >
            {componeRuta}
          </Popup>
          <Popup
            className="container-favorite"
            open={this.state.openR}
            closeOnDocumentClick={false}
            >
            <div>
              <h3 className="favorite-text">Â¿Esta son las direcciones de la carrera?</h3>
              <button onClick={() => {
                console.log("Guardar ruta")
                this.closeRoute();
              }} className="button-favorite">Guardar carrera</button>
              <button onClick={() => {
                this.closeRoute();
                this.deleteRouting();}} className="button-favorite">Volver a escoger</button>
