//Dependencias
import React, { Component } from 'react';
import { BrowserRouter as Router} from 'react-router-dom';
import { Route, Redirect } from 'react-router-dom';

//Componentes
import Main from './components/main/main';
import Login from './components/main/login';
import Navigation from './components/navigation';
import Footer from './components/footer';
import FormClient from './components/forms/client';
import FormDriver from './components/forms/driver';
import Maps from './components/map/maps';
import WindowDriver from './components/window/windowDriver';
import WindowUser from './components/window/windowUser';
import ErrorPage from './components/errorpage';


const isAuthenticated = true;

const NotLoggedRoute = ({component: Component, ...rest}) => (

  <Route { ...rest } render={() => (
    isAuthenticated === true?
    <Component />
    : <Redirect to= "/"/>
  )}/>
)

const LoggedRoute = ({component: Component, ...rest}) => (

  <Route { ...rest } render={() => (
    isAuthenticated === false?
    <Component />
    : <Redirect to= "/MainUser"/>
  )}/>
)


class App extends Component{  

  constructor(props) {
    super(props);

    this.state = {
      message: ''
    }


  }

  componentDidMount() {
    fetch('/main')
    .then(res => res.json())
    .then(res => this.setState({message: res.status}))
    .catch(err => console.log(err));



  }


  render() {

    /*
    if(this.state.message == "User is logged"){
      isAuthenticated == true;
    }else {
      isAuthenticated == false;
    }
    */

    return(
      <div>
        <Navigation />
        <Router>
          <div>
                <LoggedRoute path="/" component={Main}/>
                <LoggedRoute path="/Login" component={Login}/>
                <LoggedRoute path="/FormUser" component={FormClient}/>
                <LoggedRoute path="/FormDriver" component={FormDriver}/>

                <NotLoggedRoute path="/Maps" component={Maps}/>
                <NotLoggedRoute path="/MainDriver" component={WindowDriver}/>
                <NotLoggedRoute path="/MainUser" component={WindowUser}/>
          </div>
        </Router>        
        <Footer />
      </div>

    );
  }
}

export default App;
