//Dependencias
import React, { Component } from 'react';
import L from 'leaflet';
import 'leaflet-routing-machine';
import Popup from 'reactjs-popup';


//Componentes
import '../styles.css';
import Ruta from '../images/placeholder.png'
import Cancel from '../images/cancel-button.png'

//Variables globales
var myIcon = L.icon({
  iconUrl: 'data:image/png;base64,',
  iconSize: [25,41]
});

var myFavorites = L.icon({
  iconUrl: 'data:image/svg+xml;utf8;base64,',
  iconSize: [60,30]
});

var map;
var routingline;
var markerorigen;
var markerdestino;
var coordstemp;

class Maps extends Component {

    constructor(props){
        super(props);

        //Metodos
        this.initMap = this.initMap.bind(this);
        this.handleRightClick = this.handleRightClick.bind(this);
        this.openModal = this.openModal.bind(this);
        this.closeModal = this.closeModal.bind(this);

        this.state = {
            modal: {
                open: false,
                saveFavorite: false
            },
            location: {
                lat: 0,
                lng: 0
            },
            favorites: [],
            zoom: 2
        }

    }


    //Metodo que se ejecuta despues de que todo este en el DOM
    componentDidMount () {       

        if (navigator.geolocation){
        navigator.geolocation.getCurrentPosition((Position) => {

            this.setState({
            location: {
                lat: Position.coords.latitude,
                lng: Position.coords.longitude
            },
            zoom: 16
            })
            this.initMap()
        });
        }

    }


    //Crea el mapa
    initMap() {

        map = L.map('map', {
        center: [this.state.location.lat, this.state.location.lng],
        zoom: this.state.zoom
        });

        L.tileLayer('http://{s}.tile.osm.org/{z}/{x}/{y}{r}.png', {
        attribution: 'Â© OpenStreetMap contributors'
        }).addTo(map);

        //Posicion actual
        L.marker([this.state.location.lat, this.state.location.lng], {icon: myIcon}).addTo(map)
            .on("click", (e) => {
                console.log(e.latlng.lat, e.latlng.lng);
                console.log("Me oprimiste");
            })
            .bindPopup("Tu posicion actual").openPopup();

        this.initFavorite();        

        //Agregando marcadores o indicando la ruta
        map.on('contextmenu', (e) => {

            this.handleRightClick(e);

        });

    }


    //Manejador de evento del click izquierdo
    handleRightClick(e) {

        coordstemp = e;
        this.openModal()
    }


    addFavorites() {

        var favorites = this.state.favorites;
        var temporal = [coordstemp.latlng.lat, coordstemp.latlng.lng];
          favorites.push({
            lat: temporal[0],
            lng: temporal[1]
        });
        this.setState({favorites});
        L.marker([coordstemp.latlng.lat, coordstemp.latlng.lng],{icon: myFavorites}).addTo(map)
          .on("click", (e) => {

        })
    }


    //Metodo que me genera los favoritos guardados
    initFavorite() {

        //Favoritos
        if(this.state.favorites.length > 0)
        {
          for(let element of this.state.favorites){
            L.marker([element.lat, element.lng], {icon: myFavorites}).addTo(map)
              .on("click", (e) => {

            })
          }
        }
    }


    //Metodos que se encargan de abrir y cerrar el popup
    openModal (){
        this.setState({ modal: {
            open: true
        }})
    }

    closeModal () {
        this.setState({modal: {
            open: false
        }})
    }

    render() {

        var componentePop = 
                    <div>
                        <h3 className="modal-text">Â¿QuÃ© deseas hacer?</h3>
                        <button className="button-modal"
                            onClick={ () => {
                                this.addFavorites();
                                this.setState({modal: {
                                    saveFavorite: true
                                }})
                                this.closeModal();
                        }}>Guardar favorito</button>
                        <button className="button-modal"
                            onClick={ () => {
                                this.closeModal();
                        }}>Ir a este lugar</button>
                        <button className="button-modal"
                            onClick={ () => {
                                this.closeModal();
                        }}>Cancelar</button>
                    </div>;

        if(this.state.modal.saveFavorite === true){

            componentePop = 
                    <div>

                    </div>

        }

        return(
            <div>
                <div id="map" className="map"></div>
                <Popup
                    className="container-modal"
                    open={this.state.modal.open}
                    closeOnDocumentClick={false}
                    onClose={this.closeModal}>
                    {componentePop}
                </Popup>
            </div>

        );
    }

}

export default Maps;
