//Dependencias
import React, { Component } from 'react';
import L from 'leaflet';
import 'leaflet-routing-machine'
//import RoutingMachine from './routing-machine'

//Componentes
import '../styles.css'

var myIcon = L.icon({
  iconUrl: 'data:image/png;base64,',
  iconSize: [25,41],
  iconAnchor: [12.5,41],
  popupAnchor: [0,-41]
});

class Maps extends Component {

  state = {
    location: {
      lat: 0,
      lng: 0
    },
    zoom: 2,
    markers: []
  }


  componentDidMount () {

    if (navigator.geolocation){
      navigator.geolocation.getCurrentPosition((Position) => {

        this.setState({
          location: {
            lat: Position.coords.latitude,
            lng: Position.coords.longitude
          },
          zoom: 16
        })

        this.initMap()
      });
    }          
  }


  initMap() {/*
    var map = L.map('map', {
      center: [this.state.location.lat, this.state.location.lng],
      zoom: this.state.zoom
    });

    L.tileLayer('http://{s}.tile.osm.org/{z}/{x}/{y}{r}.png', {
      attribution: '© OpenStreetMap contributors'
    }).addTo(map);

    L.marker([this.state.location.lat, this.state.location.lng], {icon: myIcon}).addTo(map);

    map.on('contextmenu', (e) => {
      const {markers} = this.state
      markers.push(e.latlng);
      this.setState({markers});
      L.marker([e.latlng.lat, e.latlng.lng],{icon: myIcon}).addTo(map);
    });

    L.Routing.

    */



var map = L.map('map').setView([41.9168, -8.14311], 8);

L.tileLayer('http://{s}.tile.osm.org/{z}/{x}/{y}.png', {
  maxZoom: 18,
  attribution: '&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
}).addTo(map);

var groups = {
};

var layercontrol = L.control.layers(null, groups).addTo(map);

//var router = L.Routing.osrm({});
// using the "new", because of the bug on L.Routing.osrmv1
var router = new L.Routing.OSRMv1({});

var routewaypoints = [
    L.Routing.waypoint(L.latLng(41.9168, -8.54311)),
    L.Routing.waypoint(L.latLng(41.6656, -8.40248))
  ],
  routeplan = L.Routing.plan(routewaypoints);

router.route(routewaypoints, function(error, routes) {
  var route1line = L.Routing.line(routes[0]);
  var route1group = L.layerGroup([routeplan, route1line]).addTo(map);
  layercontrol.addOverlay(route1group, "LayerRoute1");
}, null, {});


console.log('Distance: ' + routes[0].summary.totalDistance);

  }


  render() {
    return (
        <div id="map" className="map"></div>
    );
  }
}

export default Maps;
