//Dependencias
import React, { Component } from 'react';
import { BrowserRouter as Router} from 'react-router-dom';
import { Route } from 'react-router-dom';

//Componentes
import Main from './components/main/main';
import Login from './components/main/login';
import Navigation from './components/navigation';
import Footer from './components/footer';
import FormClient from './components/forms/client';
import FormDriver from './components/forms/driver';
import Maps from './components/map/maps';



class App extends Component{  


  constructor() {
    super();

    this.state = {
      estado: "On-line"
    };

  }
/*

  componentDidMount() {
    fetch('/backend')
    .then(res => res.json())
    .then(status => this.setState({status}, console.log(status)));

  }
*/

  render() {
    return(
      <div>
        <Navigation />
        <Router>
          <div>
            <Route path="/" exact render={
              () => {

                if (this.state.estado == "Off-line"){

                  return (<Main />);
                }
                else {

                  return(<div></div>);
                }

              }
            }/>
            <Route path="/Login" exact render={
              () => {

                if (this.state.estado == "Off-line"){

                  return (<Login />);
                }
                else {
                  return(<div></div>);
                }
              }
            }/>
            <Route path="/FormClient" exact render={
              () => {

                if (this.state.estado == "Off-line"){

                  return (<FormClient />);
                }
                else {

                  return(<div></div>);
                }                
              }
            }/>
            <Route path="/FormDriver" exact render={
              () => {

                if (this.state.estado == "Off-line"){

                  return (<FormDriver />);
                }
                else {

                  return(<div></div>);
                }   
              }
            }/>
            <Route path="/Maps" exact render={
              () => {
                return (<Maps/>);
              }
            }/>
          </div>        
        </Router>        
        <Footer />
      </div>

    );
  }
}

export default App;

