//Dependencias
import React, { Component } from 'react';
import L from 'leaflet';
import 'leaflet-routing-machine'

//Componentes
import '../styles.css';
import Ruta from '../images/ruta.png'

var myIcon = L.icon({
  iconUrl: 'data:image/png;base64,',
  iconSize: [25,41],
  iconAnchor: [12.5,41],
  popupAnchor: [0,-41]
});

var map;
var routingline;

class Maps extends Component {

  //Constructor
  constructor(props) {
    super(props);

    this.createRouting = this.createRouting.bind(this);
    this.initMap = this.initMap.bind(this);
    this.state = {
      location: {
        lat: 0,
        lng: 0
      },
      favorites: [],
      zoom: 2,
      waypoints: {
        activo: 0,
        origen: {},
        destino: {}
      }
    }
  }  

  //Metodo que se ejecuta despues de que todo este en el DOM
  componentDidMount () {

    if (navigator.geolocation){
      navigator.geolocation.getCurrentPosition((Position) => {

        this.setState({
          location: {
            lat: Position.coords.latitude,
            lng: Position.coords.longitude
          },
          zoom: 16
        })

        this.initMap()
      });
    }          
  }

  //Crea el mapa
  initMap() {

    map = L.map('map', {
      center: [this.state.location.lat, this.state.location.lng],
      zoom: this.state.zoom
    });

    L.tileLayer('http://{s}.tile.osm.org/{z}/{x}/{y}{r}.png', {
      attribution: '© OpenStreetMap contributors'
    }).addTo(map);

    //Posicion actual
    L.marker([this.state.location.lat, this.state.location.lng], {icon: myIcon}).addTo(map);

    //Agregando marcadores o indicando la ruta
    map.on('contextmenu', (e) => {

      if(this.state.waypoints.activo){

        if (this.state.waypoints.origen == undefined)
        {
          this.setState({
            waypoints: {
              activo: 1,
              origen: {
                lat: e.latlng.lat, 
                lng: e.latlng.lng,
                marker: L.marker([e.latlng.lat, e.latlng.lng],{icon: myIcon}).addTo(map)}
          }});
        }
        else if (this.state.waypoints.destino == undefined){
          this.setState({
            waypoints: {
              activo: 1,
              origen: {
                lat: this.state.waypoints.origen.lat,
                lng: this.state.waypoints.origen.lng,

              },
              destino: {
                lat: e.latlng.lat, 
                lng: e.latlng.lng,
                marker: L.marker([e.latlng.lat, e.latlng.lng],{icon: myIcon}).addTo(map)}
          }});

          var router = new L.Routing.OSRMv1({});

          var routewaypoints = [
            L.Routing.waypoint(L.latLng(this.state.waypoints.origen.lat,this.state.waypoints.origen.lng)),
            L.Routing.waypoint(L.latLng(this.state.waypoints.destino.lat,this.state.waypoints.destino.lng))]

          router.route(routewaypoints, function(error, routes) {
            routingline = L.Routing.line(routes[0]).addTo(map);
            console.log('Distance: ' + (routes[0].summary.totalDistance / 1000) + ' Km');
            console.log('Time: ' + (routes[0].summary.totalTime / 60) + ' Min');}, 
          null, {});

        }
      }
      else {
        /*
        var waypoints = this.state.locationrouting.waypoints;
        waypoints.push(e.latlng);
        console.log(e.latlng);
        this.setState({waypoints});
        console.log(this.state.locationrouting.waypoints)
        L.marker([e.latlng.lat, e.latlng.lng],{icon: myIcon}).addTo(map);
        */
      }


    });

  }

  //Metodo que activa el modo ruta
  createRouting() {
    console.log("Preparando la ruta");

    if (this.state.waypoints.activo) {

      map.removeLayer(routingline);
      map.removeLayer(this.state.waypoints.origen.marker);
      map.removeLayer(this.state.waypoints.destino.marker);

      this.setState({
        waypoints: {
          activo: 0,
          origen: {},
          destino: {}
        }
      });
    }
    else {
      this.setState({
        waypoints: {
          activo: 1
        }
      });
    }

  }


  render() {
    return (
      <div> 
        <img onClick={this.createRouting} className="image-ruta" src={Ruta} alt="Ruta" width="50"/>
        <div id="map" className="map"></div>

      </div>
    );
  }
}

export default Maps;
