//Dependencias
import React, { Component } from 'react';
import { BrowserRouter as Router} from 'react-router-dom';
import { Route } from 'react-router-dom';

//Componentes
import Main from './components/main/main';
import Login from './components/main/login';
import Navigation from './components/navigation';
import Footer from './components/footer';
import FormClient from './components/forms/client';
import FormDriver from './components/forms/driver';
import Maps from './components/map/maps';


class App extends Component{

  state = {
    location: {
      lat: 0,
      lng: 0
    },
    haveUserLocation: false,
    zoom: 2

  }

  componentDidMount() {
    navigator.geolocation.getCurrentPosition((position) => {
      this.setState({
        location: {
          lat: position.coords.latitude,
          lng: position.coords.longitude
        },
        haveUserLocation: true,
        zoom: 15
      });
    }, () => {
      console.log("Error, no se logro obtner la direccion del navegador");
      fetch('https://ipapi.co/json')
      .then(res => res.json())
      .then(location => {
        this.setState({
          location: {
            lat: location.latitude,
            lng: location.longitude
          },
          haveUserLocation: true,
          zoom: 15
        });
      })
    })
  }

  render() {
    return(
      <div>
        <Navigation />
        <Router>
          <div> 
            <Route path="/" exact render={
              () => {
                return (<Main />);
              }
            }/>
            <Route path="/Login" exact render={
              () => {
                return (<Login />);
              }
            }/>
            <Route path="/FormClient" exact render={
              () => {
                return (<FormClient />);
              }
            }/>
            <Route path="/FormDriver" exact render={
              () => {
                return (<FormDriver />);
              }
            }/>
            <Route path="/Maps" exact render={
              () => {
                return (<Maps state={this.state}/>);
              }
            }/>
          </div>        
        </Router>
        <Footer />
      </div>

    );
  }
}

export default App;


