//Dependencias
import React, { Component } from 'react';
import { BrowserRouter as Router} from 'react-router-dom';
import { Route, Redirect } from 'react-router-dom';

//Componentes
import Main from './components/main/main';
import Login from './components/main/login';
import Navigation from './components/navigation';
import Footer from './components/footer';
import FormClient from './components/forms/client';
import FormDriver from './components/forms/driver';
import Maps from './components/map/maps';
import WindowDriver from './components/window/windowDriver';
import WindowUser from './components/window/windowUser';
import ErrorPage from './components/errorpage';


var isAuthenticated = false;


const LoggedRoute = ({component: Component, ...rest}) => (

  <Route { ...rest } render={() => {
    if(isAuthenticated == true){
      return <Component />
    }
    else {
      return<Redirect to='/' />
    }
  }}/>
)


class App extends Component{  

  constructor(props) {
    super(props);

    this.state = {
      message: ''
    }


  }

  componentWillMount() {

    fetch('/main')
    .then(res => res.json())
    .then(res => this.setState({message: res.status}))
    .catch(err => console.log(err));

  }

  componentDidMount() {




  }


  render() {

    console.log(this.state.message)

    if(this.state.message == "User is logged"){

      isAuthenticated = true;
    }
    else {

      isAuthenticated = false;
    }

    console.log(isAuthenticated)

    return(
      <div>
        <Navigation />
        <Router>
          <div>
                <Route exact path="/" component={Main}/>
                <Route exact path="/Login" component={Login}/>
                <Route exact path="/FormUser" component={FormClient}/>
                <Route exact path="/FormDriver" component={FormDriver}/>

                <LoggedRoute exact path="/Maps" component={Maps}/>
                <LoggedRoute exact path="/MainDriver" component={WindowDriver}/>
                <LoggedRoute exact path="/MainUser" component={WindowUser}/>
          </div>
        </Router>        
        <Footer />
      </div>

    );
  }
}

export default App;
